class Canvas extends React.Component {

  componentDidMount() {
    const rawCoordinates = this.props.data;
    const coords = rawCoordinates.geometries;
    const canvas = this.refs.canvas;
    const ctx = canvas.getContext("2d");
    ctx.font = "40px Courier"
    // ctx.fillRect(coords[0][0], coords[0][1], coords[1][0], coords[1][1])
    console.log(rawCoordinates)
    console.log(coords)

    ctx.clearRect(0, 0, ctx.width, ctx.height)
    ctx.beginPath();
    for (let i = 0; i < coords.length; i++) {
      ctx.lineTo(coords[i][0], coords[i][1]);
    }
    ctx.lineTo(coords[0][0], coords[0][1]);
    ctx.stroke();
  }

  render() {
    return (
      <div>
        <canvas ref="canvas" width={1000} height={500} />
      </div>
    )
  }
}

export default Canvas;
