class CoordinatesForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: '' };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <div class='coords-form'>
        <label>
          x1:
          <input type="number" value={this.state.value} onChange={this.handleChange} min="1" max="1000" />
        </label>
        <label>
          y1:
          <input type="number" value={this.state.value} onChange={this.handleChange} min="1" max="1000" />
        </label>
        <label>
          x2:
          <input type="number" value={this.state.value} onChange={this.handleChange} min="1" max="1000" />
        </label>
        <label>
          y2:
          <input type="number" value={this.state.value} onChange={this.handleChange} min="1" max="1000" />
        </label>

        <input type="submit" value="Submit" />
        </div>
        <style jsx>{`

        label {
          text-decoration: none;
          color: blue;
          font-size: 16px;
          margin: 10px;
        }

        ul {
          padding: 0;
        }

        li {
          list-style: none;
          margin: 5px 0;
        }

        a {
          text-decoration: none;
          color: blue;
          font-size: 16px;
        }
      `}</style>
      </form>

    );
  }
}

export default CoordinatesForm;
