class CoordinatesForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      x1: 1,
      y1: 1,
      x2: 0,
      y2: 0,
      x3: 0,
      y3: 0,
      x4: 0,
      y4: 0,
     };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({
      x1: 1,
      y1: 1,
      x2: 0,
      y2: 0,
      x3: 0,
      y3: 0,
      x4: 0,
      y4: 0,
    }
  );
}

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          x1:
          <input type="number" value={this.state.x1} onChange={this.handleChange} min="1" max="1000" />
        </label>
        <label>
          y1:
          <input type="number" value={this.state.y1} onChange={this.handleChange} min="1" max="1000" />
        </label>
        <label>
          x2:
          <input type="number" value={this.state.x2} onChange={this.handleChange} min="1" max="1000" />
        </label>
        <label>
          y2:
          <input type="number" value={this.state.y2} onChange={this.handleChange} min="1" max="1000" />
        </label><br></br>
        <label>
          x3:
          <input type="number" value={this.state.x3} onChange={this.handleChange} min="1" max="1000" />
        </label>
        <label>
          y3:
          <input type="number" value={this.state.y3} onChange={this.handleChange} min="1" max="1000" />
        </label>
        <label>
          x4:
          <input type="number" value={this.state.x4} onChange={this.handleChange} min="1" max="1000" />
        </label>
        <label>
          y4:
          <input type="number" value={this.state.y4} onChange={this.handleChange} min="1" max="1000" />
        </label>

        <input type="submit" value="Submit" />
        <style jsx>{`
        label {
          text-decoration: none;
          color: blue;
          font-size: 16px;
          margin: 10px;
        }

        input[type=submit] {
          background-color: #4CAF50;
          border: none;
          color: white;
          padding: 10px 10px;
          text-decoration: none;
          margin: 4px 2px;
          cursor: pointer;
        }
      `}</style>
      </form>

    );
  }
}

export default CoordinatesForm;
