class Canvas extends React.Component {

  componentDidMount() {
    const rawCoordinates = this.props.data;
    const coords = JSON.parse(rawCoordinates).geometries;
    const canvas = this.refs.canvas;
    const ctx = canvas.getContext("2d");
    ctx.font = "40px Courier"
    // ctx.fillRect(coords[0][0], coords[0][1], coords[1][0], coords[1][1])
    console.log(rawCoordinates)
    console.log(coords)

    ctx.beginPath();
    coords.map((c, index) => {
      ctx.lineTo(c[index][0], c[index][1]);
    })

    ctx.lineTo(coords[1][0], coords[1][1]);
    ctx.stroke();
  }

  render() {
    return (
      <div>
        <canvas ref="canvas" width={640} height={425} />
      </div>
    )
  }
}

export default Canvas;
