const axios = require('axios');
import Canvas from './Canvas';

class CoordinatesForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      x1: 100,
      y1: 100,
      x2: 500,
      y2: 100,
      x3: 500,
      y3: 500,
      x4: 100,
      y4: 500,
     };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.postData = this.postData.bind(this);
  }

  handleChange(event) {
    const newState = {
      x1: event.target.x1,
      y1: event.target.y1,
      x2: event.target.x2,
      y2: event.target.y2,
      x3: event.target.x3,
      y3: event.target.y3,
      x4: event.target.x4,
      y4: event.target.y4,
    };
    this.setState(newState);

    this.postData = {
      "geometries": [
        [newState.x1, newState.y1],
        [newState.x2, newState.y2],
        [newState.x3, newState.y3],
        [newState.x4, newState.y4],
      ]
    };

    return axios.post('http://localhost:4000/geometry', postData)
      .then(function (res) {
        const show = JSON.stringify(res.data);
        return {
          show: show
        };
      })
      .catch(function (err) {
        console.log(JSON.stringify(err));
      });
}

  handleSubmit(event) {
    alert('座標を入力しました！');
    event.preventDefault();
  }

  render() {
    return (
      <div><Canvas data={this.postData} />
      <form onSubmit={this.handleSubmit}>
        <label>
          x1:
          <input type="number" value={this.state.x1} onChange={this.handleChange} min="1" max="1000" />
        </label>
        <label>
          y1:
          <input type="number" value={this.state.y1} onChange={this.handleChange} min="1" max="1000" />
        </label>
        <label>
          x2:
          <input type="number" value={this.state.x2} onChange={this.handleChange} min="1" max="1000" />
        </label>
        <label>
          y2:
          <input type="number" value={this.state.y2} onChange={this.handleChange} min="1" max="1000" />
        </label><br></br>
        <label>
          x3:
          <input type="number" value={this.state.x3} onChange={this.handleChange} min="1" max="1000" />
        </label>
        <label>
          y3:
          <input type="number" value={this.state.y3} onChange={this.handleChange} min="1" max="1000" />
        </label>
        <label>
          x4:
          <input type="number" value={this.state.x4} onChange={this.handleChange} min="1" max="1000" />
        </label>
        <label>
          y4:
          <input type="number" value={this.state.y4} onChange={this.handleChange} min="1" max="1000" />
        </label>

        <input type="submit" value="Submit" />
        <style jsx>{`
        label {
          text-decoration: none;
          color: blue;
          font-size: 16px;
          margin: 10px;
        }

        input[type=submit] {
          background-color: #4CAF50;
          border: none;
          color: white;
          padding: 10px 10px;
          text-decoration: none;
          margin: 4px 2px;
          cursor: pointer;
        }
      `}</style>
      </form>
    </div>
    );
  }
}

export default CoordinatesForm;
